import React, { useState } from "react";
import { HashRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import Chart from "react-apexcharts";

const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

const Login = () => {
    const [email, setEmail] = useState("");
    const [otp, setOtp] = useState("");
    const [userOtp, setUserOtp] = useState("");
    const [countdown, setCountdown] = useState(0);
    const [disabled, setDisabled] = useState(false);
    const navigate = useNavigate();

    const handleSendOtp = () => {
        if (!email) {
            alert("Please enter your email.");
            return;
        }
        const newOtp = generateOTP();
        setOtp(newOtp);
        alert(`Your OTP: ${newOtp}`);
        localStorage.setItem("otp", newOtp);
        setCountdown(30);
        setDisabled(true);
        const timer = setInterval(() => {
            setCountdown((prev) => {
                if (prev === 1) {
                    clearInterval(timer);
                    localStorage.removeItem("otp");
                    alert("OTP expired. Request a new one.");
                    setDisabled(false);
                }
                return prev - 1;
            });
        }, 1000);
        navigate("/validate-otp");
    };

    return (
        <div style={{ display: "flex", height: "100vh", background: "linear-gradient(135deg, #3a7bd5, #00d2ff)", alignItems: "center", justifyContent: "center" }}>
            <div style={{ display: "flex", borderRadius: "24px", overflow: "hidden", boxShadow: "0 10px 30px rgba(0,0,0,0.3)" }}>
                <div style={{ background: "#2d5f73", color: "white", padding: "40px", width: "400px", display: "flex", flexDirection: "column", justifyContent: "center" }}>
                    <h2 style={{ textAlign: "center", marginBottom: "24px", fontSize: "24px" }}>Sign In</h2>
                    <input 
                        type="email" 
                        placeholder="E-mail" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        style={{ background: "#1b3a4b", color: "white", border: "none", borderRadius: "12px", padding: "16px", marginBottom: "16px", fontSize: "20px", textAlign: "center" }}
                    />
                    <button 
                        onClick={handleSendOtp} 
                        disabled={disabled}
                        style={{ background: "linear-gradient(to bottom, #38ef7d, #11998e)", padding: "16px", border: "none", borderRadius: "12px", color: "white", fontWeight: "bold", fontSize: "18px", cursor: "pointer" }}
                    >
                        Send Otp
                    </button>
                </div>
                <div style={{ background: "#3a7bd5", color: "white", padding: "40px", width: "400px", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "24px" }}>
                    Web Application with Analytics Dashboard
                </div>
            </div>
        </div>
    );
};

const ValidateOtp = () => {
    const [userOtp, setUserOtp] = useState("");
    const navigate = useNavigate();

    const verifyOTP = () => {
        const storedOtp = localStorage.getItem("otp");
        if (userOtp === storedOtp) {
            navigate("/dashboard");
        } else {
            alert("Invalid or expired OTP. Request a new one.");
        }
    };

    return (
        <div style={{ display: "flex", height: "100vh", background: "linear-gradient(135deg, #3a7bd5, #00d2ff)", alignItems: "center", justifyContent: "center" }}>
            <div style={{ display: "flex", borderRadius: "24px", overflow: "hidden", boxShadow: "0 10px 30px rgba(0,0,0,0.3)" }}>
                <div style={{ background: "#2d5f73", color: "white", padding: "40px", width: "400px", display: "flex", flexDirection: "column", justifyContent: "center" }}>
                    <h2 style={{ textAlign: "center", marginBottom: "24px", fontSize: "24px" }}>Enter OTP</h2>
                    <input 
                        type="text" 
                        placeholder="OTP" 
                        value={userOtp} 
                        onChange={(e) => setUserOtp(e.target.value)} 
                        style={{ background: "#1b3a4b", color: "white", border: "none", borderRadius: "12px", padding: "16px", marginBottom: "16px", fontSize: "20px", textAlign: "center" }}
                    />
                    <button 
                        onClick={verifyOTP} 
                        style={{ background: "linear-gradient(to bottom, #38ef7d, #11998e)", padding: "16px", border: "none", borderRadius: "12px", color: "white", fontWeight: "bold", fontSize: "18px", cursor: "pointer" }}
                    >
                        Validate OTP
                    </button>
                </div>
            </div>
        </div>
    );
};

const Dashboard = () => {
    const chartData = {
        options: {
            chart: { id: "basic-bar" },
            xaxis: { categories: ["12th Oct", "13th Oct", "14th Oct", "15th Oct", "16th Oct", "17th Oct"] }
        },
        series: [{ name: "Margin %", data: [30, 40, 80, 50, 90, 60] }]
    };

    return (
        <div style={{ minHeight: "100vh", background: "linear-gradient(to right, #1a2a6c, #b21f1f, #fdbb2d)", padding: "32px" }}>
            <h2 style={{ color: "white", fontSize: "28px", textAlign: "center", marginBottom: "24px" }}>Analytics Dashboard</h2>
            <div style={{ display: "flex", gap: "20px", justifyContent: "center", flexWrap: "wrap" }}>
                <div style={{ width: "300px", height: "200px", backgroundColor: "#fca311", borderRadius: "16px", color: "white", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "24px" }}>Inventory 93%</div>
                <div style={{ width: "300px", height: "200px", backgroundColor: "#e63946", borderRadius: "16px", color: "white", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "24px" }}>Orders 65%</div>
                <div style={{ width: "300px", height: "200px", backgroundColor: "#2a9d8f", borderRadius: "16px", color: "white", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "24px" }}>Battery 65%</div>
            </div>
            <div style={{ marginTop: "20px", background: "white", padding: "20px", borderRadius: "16px" }}>
                <Chart options={chartData.options} series={chartData.series} type="line" height={300} />
            </div>
        </div>
    );
};

const App = () => {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login />} />
                <Route path="/validate-otp" element={<ValidateOtp />} />
                <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
        </Router>
    );
};

export default App;
