import React, { useState } from "react";
import { HashRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import Chart from "react-apexcharts";
import Login from "./Login";

const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

const ValidateOtp = () => {
    const [userOtp, setUserOtp] = useState("");
    const [countdown, setCountdown] = useState(0);
    const navigate = useNavigate();

    setCountdown(30);
            setDisabled(true);
            const timer = setInterval(() => {
                setCountdown((prev) => {
                    if (prev === 1) {
                        clearInterval(timer);
                        localStorage.removeItem("otp");
                        alert("OTP expired. Request a new one.");
                        setDisabled(false);
                    }
                    return prev - 1;
                });
            }, 1000);

    const verifyOTP = () => {
        const storedOtp = localStorage.getItem("otp");
        if (userOtp === storedOtp) {
            navigate("/dashboard");
        } else {
            alert("Invalid or expired OTP. Request a new one.");
        }
    };

    return (

        <div style={{ display: "flex", height: "100vh", background: "linear-gradient(135deg, #3a7bd5, #00d2ff)", alignItems: "center", justifyContent: "center" }}>
            <div style={{ display: "flex", borderRadius: "24px", overflow: "hidden", boxShadow: "0 10px 30px rgba(0,0,0,0.3)" }}>
                <div style={{ background: "#2d5f73", color: "white", padding: "40px", width: "400px", display: "flex", flexDirection: "column", justifyContent: "center" }}>
                    
                    <h2 style={{ textAlign: "center", marginBottom: "24px", fontSize: "24px" }}>Enter Otp sent to Email</h2>
                    <input 
                        type="text" 
                        placeholder="OTP" 
                        value={userOtp} 
                        onChange={(e) => setUserOtp(e.target.value)} 
                        style={{ background: "#1b3a4b", color: "white", border: "none", borderRadius: "12px", padding: "16px", marginBottom: "16px", fontSize: "20px", textAlign: "center" }}
                    />
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px", fontSize: "14px" }}>
                        <span style={{ cursor: "pointer", textDecoration: "underline" }} onClick={Login.handleSendOtp}>resend otp</span>
                        <span>{countdown > 0 ? `${countdown} sec` : "0:00 sec"}</span>
                    </div>
                    <button 
                        onClick={verifyOTP} 
                        style={{ background: "linear-gradient(to bottom, #38ef7d, #11998e)", padding: "16px", border: "none", borderRadius: "12px", color: "white", fontWeight: "bold", fontSize: "18px", cursor: "pointer" }}
                    >
                        Validate
                    </button>
                </div>
                <div style={{ background: "#3a7bd5", color: "white", padding: "40px", width: "400px", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "24px" }}>
                    Web Application with Analytics Dashboard
                </div>
            </div>
        </div>
    );
};


export default ValidateOtp;
