import React, { useState } from "react";
import { HashRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import Chart from "react-apexcharts";

const generateOTP = () => Math.floor(100000 + Math.random() * 900000).toString();

const Login = () => {
    const [email, setEmail] = useState("");
    const [otp, setOtp] = useState("");
    const [userOtp, setUserOtp] = useState("");
    const [countdown, setCountdown] = useState(0);
    const [disabled, setDisabled] = useState(false);
    const navigate = useNavigate();

    const handleSendOtp = () => {
        if (!email) {
            alert("Please enter your email.");
            return;
        }
        const newOtp = generateOTP();
        setOtp(newOtp);
        alert(`Your OTP: ${newOtp}`);
        localStorage.setItem("otp", newOtp);
        setCountdown(30);
        setDisabled(true);
        const timer = setInterval(() => {
            setCountdown((prev) => {
                if (prev === 1) {
                    clearInterval(timer);
                    localStorage.removeItem("otp");
                    alert("OTP expired. Request a new one.");
                    setDisabled(false);
                }
                return prev - 1;
            });
        }, 1000);
    };

    const verifyOTP = () => {
        const storedOtp = localStorage.getItem("otp");
        if (userOtp === storedOtp) {
            navigate("/dashboard");
        } else {
            alert("Invalid or expired OTP. Request a new one.");
        }
    };

    return (
        <div style={{ display: "flex", alignItems: "center", justifyContent: "center", height: "100vh", background: "linear-gradient(to right, #63b3ed, #48bb78)" }}>
            <div style={{ background: "linear-gradient(135deg, #6e45e2, #88d3ce)", color: "white", padding: "32px", borderRadius: "16px", boxShadow: "0 10px 20px rgba(0, 0, 0, 0.3)", width: "400px", textAlign: "center" }}>
                <h2 style={{ fontSize: "24px", marginBottom: "20px" }}>Sign In</h2>
                <input 
                    type="email" 
                    placeholder="Enter Email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)} 
                    style={{ width: "100%", padding: "12px", marginBottom: "20px", color: "black", border: "none", borderRadius: "8px", boxShadow: "inset 4px 4px 6px rgba(0, 0, 0, 0.2)", fontSize: "16px" }}
                />
                <button 
                    onClick={handleSendOtp} 
                    disabled={disabled}
                    style={{ background: "linear-gradient(to bottom, #38ef7d, #11998e)", padding: "12px 24px", border: "none", borderRadius: "12px", cursor: disabled ? "not-allowed" : "pointer", color: "white", fontWeight: "bold", fontSize: "16px", marginBottom: "20px" }}
                >
                    Send OTP
                </button>
                {countdown > 0 && <p style={{ marginTop: "16px" }}>Time remaining: {countdown}s</p>}
                {otp && (
                    <div>
                        <input 
                            type="text" 
                            placeholder="Enter OTP"
                            value={userOtp} 
                            onChange={(e) => setUserOtp(e.target.value)} 
                            style={{ width: "100%", padding: "12px", marginTop: "20px", color: "black", border: "none", borderRadius: "8px", boxShadow: "inset 4px 4px 6px rgba(0, 0, 0, 0.2)", fontSize: "16px" }}
                        />
                        <button 
                            onClick={verifyOTP} 
                            style={{ background: "linear-gradient(to bottom, #38ef7d, #11998e)", padding: "12px 24px", border: "none", borderRadius: "12px", cursor: "pointer", color: "white", fontWeight: "bold", fontSize: "16px", marginTop: "20px" }}
                        >
                            Verify OTP
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

const Dashboard = () => {
    const chartData = {
        options: {
            chart: { id: "basic-bar" },
            xaxis: { categories: ["Jan", "Feb", "Mar", "Apr", "May"] }
        },
        series: [{ name: "Inventory", data: [80, 90, 70, 85, 95] }]
    };

    return (
        <div style={{ minHeight: "100vh", background: "linear-gradient(to right, #63b3ed, #48bb78)", padding: "32px" }}>
            <h2 style={{ color: "white", fontSize: "28px", textAlign: "center", marginBottom: "24px" }}>Analytics Dashboard</h2>
            <div style={{ display: "flex", alignItems: "center", justifyContent: "center", height: "60vh", borderRadius: "16px", background: "linear-gradient(135deg, #6e45e2, #88d3ce)", boxShadow: "0 10px 20px rgba(0, 0, 0, 0.3)" }}>
                <h3 style={{ color: "white", fontSize: "20px" }}>Web Application with Analytics Dashboard</h3>
            </div>
        </div>
    );
};

const App = () => {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login />} />
                <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
        </Router>
    );
};

export default App;
